def merge_the_tools(string, k):
    # your code goes here
    n = len(string)
    print('n:',n)
    print(f'First character is {string[0]}, last character is {string[-1]}')
    
    if (1<=n) and (n<=1e4) and (1<=k) and (k<=n):
        u = ''
        for i in range(n):
            if (i>0) and (i % k == 0):
                print(u)
                u = ''
            if string[i] not in u:
                u += string[i]
        print(u)
    else:
        print("Indices out of range")

if __name__ == '__main__':
    string, k = input().strip(), int(input())
    # k = 3452
    # string = ""
    merge_the_tools(string, k)